import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [userData, setUserData] = useState({
    user: "",
    email: "",
    phone: "",
  });

  const [editingID, setEditingID] = useState(1);
  const [editingUser, setEditingUser] = useState(null);
  const [imageUrl, setImageUrl] = useState("");

  const [nameError, setNameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const nameRegex = /^[A-Za-z]{2,}$/;
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const phoneRegex = /^\d{10}$/;


 

  useEffect(() => {
    // Fetch the user data when the component mounts
    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => setUserData(response.data))
      .catch((error) => console.error(error));
  }, [editingID]);

  const handleEditUser = (user) => {
    setEditingUser(user);
  };

  const handleUpdateUser = () => {

    if (!nameError && !emailError && !phoneError) {
    const updatedUserData = {
      ...userData,
      image: imageUrl,
    };

    axios
      .put(
        `https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`,
        updatedUserData
      )
      .then((response) => {
        setUserData(response.data);
        setEditingUser(null);
        // Clear the image URL after updating
        setImageUrl("");
      })
      .catch((error) => console.error("Error updating user:", error));

    } else {
      console.log("Form is not valid");
    }
    
  };
  return (
    <>
      <div className="container section single-movie">
        <div className="row">
          <Form>
            <div className="col-sm-4">
              <h2>Personal Information</h2>
              <div className="row">
                <div>
                  <img
                    src={userData.image}
                    className="GabrielNorrisImage"
                    alt="image"
                    width="300px"
                  />
                </div>
              </div>
            </div>

            <div className="col-sm-6 col-sm-push-1">
              <h2>Your Profile</h2>

              <div className="form-group">
                <Form.Field>
                  <label>Name: </label>
                  <input
                    type="text"
                    placeholder="enter your name"
                    value={userData.user}
                    readOnly
                  />
                </Form.Field>
              </div>
              <div className="form-group">
                <FormField>
                  <label>Email:</label>
                  <input
                    type="email"
                    placeholder="enter your email"
                    value={userData.email}
                    readOnly
                  />
                </FormField>
              </div>
              <div className="form-group">
                <FormField>
                  <label>Phone:</label>
                  <input
                    type="text"
                    placeholder="enter your phone"
                    value={userData.phone}
                    readOnly
                  />
                </FormField>
              </div>

              <div className="form-group right-align">
                <Button
                  className="btn btn-ghost"
                  type="submit"
                  onClick={() => handleEditUser(userData)}
                >
                  Edit Information
                </Button>
              </div>
            </div>
          </Form>
        </div>

        {editingUser && (
          <div className="edit-section">
            <Form>
              <h2>Edit User</h2>
              <FormField>
                <input
                  type="text"
                  placeholder="enter your name"
                  value={userData.user}
                  // onChange={(e) =>
                  //   setUserData({ ...userData, user: e.target.value })
                  // }

              
                />
                
              </FormField>
              <FormField>
                <input
                  type="text"
                  placeholder="enter your email"
                  value={userData.email}
                  // onChange={(e) =>
                  //   setUserData({ ...userData, email: e.target.value })
                  // }

                  onChange={(e) => {
                    const inputValue = { ...userData, email: e.target.value };
                    setUserData(inputValue);
                    if (!emailRegex.test(inputValue)) {
                      setEmailError("Please enter a valid email");
                    } else {
                      setEmailError("");
                    }
                  }}
                />
                {emailError && (
                    <div className="error text-danger">{emailError}</div>
                  )}
              </FormField>

              <FormField>
                <input
                  type="text"
                  placeholder="enter your phone number"
                  value={userData.phone}
                  // onChange={(e) =>
                  //   setUserData({ ...userData, phone: e.target.value })
                  // }
                  onChange={(e) => {
                    const inputValue = { ...userData, phone: e.target.value };
                    setUserData(inputValue);
                    if (!phoneRegex.test(inputValue)) {
                      setPhoneError("Please enter a valid phone");
                    } else {
                      setPhoneError("");
                    }
                  }}
                />
                {phoneError && (
                    <div className="error text-danger">{phoneError}</div>
                  )}
              </FormField>
              <FormField>
                {/* <label>Profile Image URL:</label> */}
                <input
                  type="text"
                  placeholder="enter image URL"
                  value={imageUrl}
                  onChange={(e) => setImageUrl(e.target.value)}
                />
              </FormField>
              <Button
                className="btn btn-ghost"
                onClick={handleUpdateUser}
                style={{ marginTop: "10px" }}
                disabled={!userData.user || !userData.email 
                  || !userData.phone }
              >
                Update
              </Button>
            </Form>
          </div>
        )}
      </div>
    </>
  );
};

export default PersonalDetails;
