import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [users, setUsers] = useState([]);

  const [editingID, setEditingID] = useState(1);
  const [editingName, setEditingName] = useState("");
  const [editingEmail, setEditingEmail] = useState("");
  const [editingPhone, setEditingPhone] = useState("");

  const [editingImage, setEditingImage] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Fetch the list of items when the component mounts

    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => setUsers(response.data))
      .catch((error) => console.error(error));
  }, []);

  const updateItem = () => {
    // Send a PUT request to update an existing item
    axios
      .put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`, {
        user: editingName,
        email: editingEmail,
        phone: editingPhone,
        image: imageUrl,
      })
      .then((response) => {
        const updatedItems = users.map((user) =>
          user.id === editingID ? response.data : user
        );
        setUsers(updatedItems);
        setMessage("Image updated successfully");
      })
      .catch((error) => console.error(error));
    setMessage("Error updating image");
  };

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append("image", editingImage);

      const response = await axios.post(
        `https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`,
        formData
      );

      setImageUrl(response.data.imageUrl);
      setMessage("Image uploaded successfully");
    } catch (error) {
      setMessage("Error uploading image");
    }
  };

  return (
    <>
      <div class="container section single-movie">
        <div class="row">
          <Form>
            <div class="col-sm-4">
              <h2>Personal Information</h2>
              <div class="row">
                <img
                  src="assets/images/user.png"
                  className="GabrielNorrisImage"
                  alt="image"
                  width="300px"
                />
                <div>
                  <Form.Field>
                    <label>Upload Additional File</label>
                    <input
                      type="file"
                      placeholder="Image URL"
                      value={editingImage}
                      onChange={(e) => {
                        const selectedImage = e.target.files[0];
                        setEditingImage(selectedImage);
                      }}
                    />
                    <button onClick={handleUpload}>Upload Image</button>
                  </Form.Field>
                </div>
              </div>
            </div>

            <div class="col-sm-6 col-sm-push-1">
              <h2>Edit Profile</h2>

              <div class="form-group">
                <Form.Field>
                  <label>Name: </label>
                  <input
                    type="text"
                    placeholder="enter your name"
                    value={editingName}
                    // pattern="^[A-Za-z]+$"
                    onChange={(e) => setEditingName(e.target.value)}
                  />
                </Form.Field>
              </div>
              <div class="form-group">
                <FormField>
                  <label>Email:</label>
                  <input
                    type="email"
                    placeholder="enter your email"
                    value={editingEmail}
                    onChange={(e) => setEditingEmail(e.target.value)}
                  />
                </FormField>
              </div>
              <div class="form-group">
                <FormField>
                  <label>Phone:</label>
                  <input
                    type="text"
                    placeholder="enter your phone"
                    value={editingPhone}
                    onChange={(e) => setEditingPhone(e.target.value)}
                  />
                </FormField>
              </div>

              <div class="form-group right-align">
                <Button
                  className="btn btn-ghost"
                  type="submit"
                  onClick={updateItem}
                >
                  Submit
                </Button>
              </div>
            </div>
          </Form>
        </div>
      </div>
    </>
  );
};

export default PersonalDetails;
