import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [users, setUsers] = useState([]);

  const [editingID, setEditingID] = useState(1);


  // const [newUser, setNewUser] = useState({ user: '', email: '', phone: ''});

  const [editingName, setEditingName] = useState("");
  const [editingEmail, setEditingEmail] = useState("");
  const [editingPhone, setEditingPhone] = useState("");


  const [editingUser, setEditingUser] = useState(null);



  useEffect(() => {
    // Fetch the list of items when the component mounts
    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => setUsers(response.data))
      .catch((error) => console.error(error));
  }, []);

  // const handleEditUser = (user) => {
  //   setEditingUser(user);
  //   // setNewUser({ ...user });
  //   setEditingName()
  //   setEditingEmail()
  //   setEditingPhone()
  // };


  const handleUpdateUser = () => {
    axios.put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`, { user: editingName,
          email: editingEmail,
          phone: editingPhone,})
        .then((response) => {
            setUsers(users.map((user) => (user.id === editingID ? response.data : user)));
            setEditingUser(null);
            // setNewUser({ user: '', email: '', phone: ''});

            setEditingName()
    setEditingEmail()
    setEditingPhone()
        })
        .catch(error => console.error('Error updating user:', error));
};



  // const updateItem = () => {
  //   // Send a PUT request to update an existing item
  //   axios
  //     .put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`, {
  //       user: editingName,
  //       email: editingEmail,
  //       phone: editingPhone,
  //       image: imageUrl,
  //     })
  //     .then((response) => {
  //       const updatedItems = user.map((user) =>
  //         user.id === editingID ? response.data : user
  //       );
  //       setUser(updatedItems);
  //       setMessage("Image updated successfully");
  //     })
  //     .catch((error) => console.error(error));
  //   setMessage("Error updating image");
  // };


  return (
    <>
      <div class="container section single-movie">
        <div class="row">
          <Form>
            <div class="col-sm-4">
              <h2>Personal Information</h2>
              <div class="row">
                <div>
                  <img
                    src="assets/images/user.png"
                    className="GabrielNorrisImage"
                    alt="image"
                    width="300px"
                  />
                </div>
              </div>
            </div>

            <div class="col-sm-6 col-sm-push-1">
              <h2>Your Profile</h2>

              <div class="form-group">
                <Form.Field>
                  <label>Name: </label>
                  <input
                    type="text"
                    placeholder="enter your name"
                    value={users.user}
                    readOnly
                  />
                </Form.Field>
              </div>
              <div class="form-group">
                <FormField>
                  <label>Email:</label>
                  <input
                    type="email"
                    placeholder="enter your email"
                    value={users.email}
                    readOnly
                  />
                </FormField>
              </div>
              <div class="form-group">
                <FormField>
                  <label>Phone:</label>
                  <input
                    type="text"
                    placeholder="enter your phone"
                    value={users.phone}
                    readOnly
                  />
                </FormField>
              </div>

              <div class="form-group right-align">
                <Button
                  className="btn btn-ghost"
                  type="submit"
                  onClick={() => setEditingUser(users)}
                >
                  Edit Information
                </Button>
              </div>
            </div>
          </Form>
        </div>

        {editingUser && (
                <div className="edit-section">
                  <Form>
                    <h2>Edit User</h2>
                    <FormField>
                    <input
                        type="text"
                        placeholder="Name"
                        value={editingName.user}
                        onChange={(e) => setEditingName( e.target.value )}
                    />
                    </FormField>
                    <FormField>
                    <input
                        type="text"
                        placeholder="Email"
                        value={editingEmail.email}
                        onChange={(e) => setEditingEmail( e.target.value )}
                    /></FormField>
                    <FormField>
                    <input
                        type="text"
                        placeholder="Phone"
                        value={editingPhone.phone}
                        onChange={(e) => setEditingPhone(  e.target.value )}
                    /></FormField>
                    <Button className="btn btn-ghost" onClick={handleUpdateUser} style={{ marginTop: '10px' }}>Update</Button>
                    </Form>
                </div>
              
            )}

      </div>
    </>
  );
};

export default PersonalDetails;
