import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [user, setUser] = useState([]);

  const [editingID, setEditingID] = useState(1);


  const [newUser, setNewUser] = useState({ user: '', email: '', phone: ''});
  const [editingUser, setEditingUser] = useState(null);



  useEffect(() => {
    // Fetch the list of items when the component mounts
    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => setUser(response.data))
      .catch((error) => console.error(error));
  }, []);

  const handleEditUser = (user) => {
    setEditingUser(user);
    setNewUser({ ...user });
  };


  const handleUpdateUser = () => {
    axios.put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`, newUser)
        .then((response) => {
            setUser(user.map((user) => (user.id === editingID ? response.data : user)));
            setEditingUser(null);
            setNewUser({ user: '', email: '', phone: ''});
        })
        .catch(error => console.error('Error updating user:', error));
};



  // const updateItem = () => {
  //   // Send a PUT request to update an existing item
  //   axios
  //     .put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`, {
  //       user: editingName,
  //       email: editingEmail,
  //       phone: editingPhone,
  //       image: imageUrl,
  //     })
  //     .then((response) => {
  //       const updatedItems = user.map((user) =>
  //         user.id === editingID ? response.data : user
  //       );
  //       setUser(updatedItems);
  //       setMessage("Image updated successfully");
  //     })
  //     .catch((error) => console.error(error));
  //   setMessage("Error updating image");
  // };

  // const handleUpload = async () => {
  //   try {
  //     const formData = new FormData();
  //     formData.append("image", editingImage);

  //     const response = await axios.post(
  //       `https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`,
  //       formData
  //     );

  //     setImageUrl(response.data.imageUrl);
  //     setMessage("Image uploaded successfully");
  //   } catch (error) {
  //     setMessage("Error uploading image");
  //   }
  // };

  return (
    <>
      <div class="container section single-movie">
        <div class="row">
          <Form>
            <div class="col-sm-4">
              <h2>Personal Information</h2>
              <div class="row">
                <div>
                  <img
                    src="assets/images/user.png"
                    className="GabrielNorrisImage"
                    alt="image"
                    width="300px"
                  />
                </div>
              </div>
            </div>

            <div class="col-sm-6 col-sm-push-1">
              <h2>Edit Profile</h2>

              <div class="form-group">
                <Form.Field>
                  <label>Name: </label>
                  <input
                    type="text"
                    placeholder="enter your name"
                    value={user.user}
                    readOnly
                  />
                </Form.Field>
              </div>
              <div class="form-group">
                <FormField>
                  <label>Email:</label>
                  <input
                    type="email"
                    placeholder="enter your email"
                    value={user.email}
                    readOnly
                  />
                </FormField>
              </div>
              <div class="form-group">
                <FormField>
                  <label>Phone:</label>
                  <input
                    type="text"
                    placeholder="enter your phone"
                    value={user.phone}
                    readOnly
                  />
                </FormField>
              </div>

              <div class="form-group right-align">
                <Button
                  className="btn btn-ghost"
                  type="submit"
                  onClick={() => handleEditUser(user)}
                >
                  Edit Information
                </Button>
              </div>
            </div>
          </Form>
        </div>

        {editingUser && (
                <div className="edit-section">
                  <Form>
                    <h2>Edit User</h2>
                    <FormField>
                    <input
                        type="text"
                        placeholder="Name"
                        value={newUser.user}
                        onChange={(e) => setNewUser({ ...newUser, user: e.target.value })}
                    />
                    </FormField>
                    <FormField>
                    <input
                        type="text"
                        placeholder="Email"
                        value={newUser.email}
                        onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                    /></FormField>
                    <FormField>
                    <input
                        type="text"
                        placeholder="Phone"
                        value={newUser.phone}
                        onChange={(e) => setNewUser({ ...newUser, phone: e.target.value })}
                    /></FormField>
                    <Button className="btn btn-ghost" onClick={handleUpdateUser} style={ma}>Update</Button>
                    </Form>
                </div>
              
            )}

      </div>
    </>
  );
};

export default PersonalDetails;
