import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [users, setUsers] = useState([]);

  const [editingID, setEditingID] = useState(1);
  const [editingName, setEditingName] = useState("");
  const [editingEmail, setEditingEmail] = useState("");
  const [editingPhone, setEditingPhone] = useState("");

  useEffect(() => {
    // Fetch the list of items when the component mounts
   
    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => setUsers(response.data))
      .catch((error) => console.error(error));
  }, []);



  const updateItem = () => {
    // Send a PUT request to update an existing item
    axios.put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`,
     { user: editingName ,email: editingEmail, phone: editingPhone})
      .then(response => {
        const updatedItems = users.map(user =>
          user.id === editingID ? response.data : user
        );
        
      })
      .catch(error => console.error(error));
  };

  return (
    <>
      <div class="container section single-movie">
        <div class="row">
          
            <Form>
              <div class="col-sm-4">
                <h2>Personal Information</h2>
                <div class="row">
                  <img
                    src="assets/images/user.png"
                    className="GabrielNorrisImage"
                    alt="image"
                    width="300px"
                  />
                  <div>
                    <Form.Field>
                      <label>Upload Additional File</label>
                      <input
                        type="file"
                        accept="*/*"

                        // onChange={(e) => setAdditionalFile(e.target.files[0])}
                        className="CustomFileInput" // Add your custom CSS class here
                      />
                    </Form.Field>
                  </div>
                </div>
              </div>

              <div class="col-sm-6 col-sm-push-1">
                <h2>Edit Profile</h2>

                <div class="form-group">
                  <Form.Field>
                    <label>Name: </label>
                    <input
                      type="text"
                      placeholder="enter your name"
                      value={users.user}
                      // pattern="^[A-Za-z]+$"
                      // onChange={(e) => setNewComment(e.target.value)}
                    />
                  </Form.Field>
                </div>
                <div class="form-group">
                  <FormField>
                    <label>Email:</label>
                    <input
                      type="email"
                      placeholder="enter your email"
                      value={users.email}
                    />
                  </FormField>
                </div>
                <div class="form-group">
                  <FormField>
                    <label>Phone:</label>
                    <input
                      type="text"
                      placeholder="enter your phone"
                      value={users.phone}
                    />
                  </FormField>
                </div>

                <div class="form-group right-align">
                  <Button className="btn btn-ghost" type="submit">
                    Submit
                  </Button>
                </div>
              </div>
            </Form>
        
        </div>
      </div>
    </>
  );
};

export default PersonalDetails;
