import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [editingID, setEditingID] = useState(1);
  const [editingName, setEditingName] = useState("");
  const [editingEmail, setEditingEmail] = useState("");
  const [editingPhone, setEditingPhone] = useState("");
  const [editingImage, setEditingImage] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Fetch user data when the component mounts
    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => {
        setEditingName(response.data.user);
        setEditingEmail(response.data.email);
        setEditingPhone(response.data.phone);
        setImageUrl(response.data.image);
      })
      .catch((error) => console.error(error));
  }, [editingID]);

  const updateImage = async () => {
    try {
      const formData = new FormData();
      formData.append("image", editingImage);

      const response = await axios.put(
        `https://651d753144e393af2d59d664.mockapi.io/users/${editingID}/image`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setImageUrl(response.data.imageUrl);
      setMessage("Image uploaded successfully");
    } catch (error) {
      setMessage("Error uploading image");
    }
  };

  const updateUser = async () => {
    try {
      await axios.put(
        `https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`,
        {
          user: editingName,
          email: editingEmail,
          phone: editingPhone,
        }
      );
      setMessage("User data updated successfully");
    } catch (error) {
      setMessage("Error updating user data");
    }
  };

  return (
    <>
      {/* Your form and UI components here */}
      <div>
        <label>Upload Additional File</label>
        <input
          type="file"
          onChange={(e) => {
            const selectedImage = e.target.files[0];
            setEditingImage(selectedImage);
          }}
        />
        <Button onClick={updateImage}>Upload Image</Button>
      </div>
      {/* Rest of your form and UI components */}
    </>
  );
};

export default PersonalDetails;
