import React, { useState, useEffect } from "react";
import axios from "axios";

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [newRating, setNewRating] = useState(0); // Start with 0 rating

  useEffect(() => {
    axios
      .get("https://651c0dd4194f77f2a5af4f26.mockapi.io/review")
      .then((response) => setReviews(response.data))
      .catch((error) => console.error(error));
  }, [newComment, newRating]); // Include newComment and newRating in the dependency array

  const addItem = () => {
    axios
      .post("https://651c0dd4194f77f2a5af4f26.mockapi.io/review", {
        comment: newComment,
        rating: newRating,
        date: new Date().toLocaleDateString() // Current date
      })
      .then((response) => {
        setReviews([...reviews, response.data]);
        setNewComment("");
        setNewRating(0); // Reset rating after submission
      })
      .catch((error) => console.error(error));
  };

  const handleStarClick = (starValue) => {
    setNewRating(starValue);
  };

  return (
    <>
      {/* ... (your other JSX code) /}
      <div className="col-sm-2">
        <img src="assets/images/user.png" alt="User" className="user-avatar" />
      </div>
      {/ ... (rest of your JSX code) /}
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map((starValue) => (
          <span
            key={starValue}
            className={star ${starValue <= newRating ? "active" : ""}}
            onClick={() => handleStarClick(starValue)}
          >
            â˜…
          </span>
        ))}
      </div>
      {/ ... (rest of your JSX code) */}
    </>
  );
};

export default Reviews;
