import React, { useState, useEffect } from "react";
import { Button, Form, FormField } from "semantic-ui-react";
import axios from "axios";

const PersonalDetails = () => {
  const [user, setUser] = useState([]);
  const [editingID, setEditingID] = useState(1);
  const [newUser, setNewUser] = useState({ user: '', email: '', phone: '' });
  const [editingUser, setEditingUser] = useState(null);
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    // Fetch the user data when the component mounts
    axios
      .get(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`)
      .then((response) => setUser(response.data))
      .catch((error) => console.error(error));
  }, [editingID]);

  const handleEditUser = (userData) => {
    setEditingUser(userData);
    setNewUser({ ...userData });
  };

  const handleUpdateUser = () => {
    const updatedUserData = { ...newUser, image: imageUrl };

    axios
      .put(`https://651d753144e393af2d59d664.mockapi.io/users/${editingID}`, updatedUserData)
      .then((response) => {
        setUser(user.map((userData) => (userData.id === editingID ? response.data : userData)));
        setEditingUser(null);
        setNewUser({ user: '', email: '', phone: '' });
        setImageUrl('');
      })
      .catch((error) => console.error('Error updating user:', error));
  };

  return (
    <>
      <div className="container section single-movie">
        <div className="row">
          <Form>
            <div className="col-sm-4">
              <h2>Personal Information</h2>
              <div className="row">
                <div>
                  <img
                    src={imageUrl || "assets/images/user.png"} // Display the updated image URL or a default image
                    className="GabrielNorrisImage"
                    alt="image"
                    width="300px"
                  />
                </div>
              </div>
            </div>

            <div className="col-sm-6 col-sm-push-1">
              <h2>Your Profile</h2>

              <div className="form-group">
                <Form.Field>
                  <label>Name: </label>
                  <input
                    type="text"
                    placeholder="enter your name"
                    value={user.user}
                    readOnly
                  />
                </Form.Field>
              </div>
              <div className="form-group">
                <FormField>
                  <label>Email:</label>
                  <input
                    type="email"
                    placeholder="enter your email"
                    value={user.email}
                    readOnly
                  />
                </FormField>
              </div>
              <div className="form-group">
                <FormField>
                  <label>Phone:</label>
                  <input
                    type="text"
                    placeholder="enter your phone"
                    value={user.phone}
                    readOnly
                  />
                </FormField>
              </div>

              <div className="form-group right-align">
                <Button
                  className="btn btn-ghost"
                  type="submit"
                  onClick={() => handleEditUser(user)}
                >
                  Edit Information
                </Button>
              </div>
            </div>
          </Form>
        </div>

        {editingUser && (
          <div className="edit-section">
            <Form>
              <h2>Edit User</h2>
              <FormField>
                <input
                  type="text"
                  placeholder="Name"
                  value={newUser.user}
                  onChange={(e) => setNewUser({ ...newUser, user: e.target.value })}
                />
              </FormField>
              <FormField>
                <input
                  type="text"
                  placeholder="Email"
                  value={newUser.email}
                  onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                />
              </FormField>
              <FormField>
                <input
                  type="text"
                  placeholder="Phone"
                  value={newUser.phone}
                  onChange={(e) => setNewUser({ ...newUser, phone: e.target.value })}
                />
              </FormField>
              <FormField>
                <label>Profile Image URL:</label>
                <input
                  type="text"
                  placeholder="enter image URL"
                  value={imageUrl}
                  onChange={(e) => setImageUrl(e.target.value)}
                />
              </FormField>
              <Button className="btn btn-ghost" onClick={handleUpdateUser} style={{ marginTop: '10px' }}>Update</Button>
            </Form>
          </div>
        )}
      </div>
    </>
  );
};

export default PersonalDetails;
