import React, { useState, useEffect } from "react";
import axios from "axios";

const Reviews = () => {
  const [reviews, setReviews] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [newRating, setNewRating] = useState("");

  //   const [newDate, setNewDate] = useState("");

  useEffect(() => {
    // Fetch the list of items when the component mounts
    axios
      .get("https://651c0dd4194f77f2a5af4f26.mockapi.io/comments")
      .then(response => setReviews(response.data))
      .catch(error => console.error(error));
  }, []);

  const addItem = () => {
    // Send a POST request to create a new item

    axios
      .post("https://651c0dd4194f77f2a5af4f26.mockapi.io/comments", {
        newComment,
         newRating,
      })
      .then((response) => {
		console.log('Created:', response.data);
        window.location.href = "#";
        // setReviews([...reviews, response.data]);
        // setNewComment("");
        // setNewRating(0);
      })
      .catch((error) => console.error(error));
   
  };

  const handleStarClick = (starValue) => {
    setNewRating(starValue);
  };

  return (
    <>
      <div className="container section single-movie">
        <div className="row">
          <div className="col-sm-7">
            <h2>Comments</h2>
            <div className="comments">
              {reviews.map((review) => (
                <div className="row" key={review.id}>
                  <div className="col-sm-2">
                    <img
                      src="assets/images/user.png" className="Luke Barrett"
                    />
                  </div>
                  <div className="col-sm-9">
                    {/* <span className="date">{review.date}</span> */}
                    <h4 className="no-underline">Luke Barrett</h4>

                    <div className="star-rating">
                      {[1, 2, 3, 4, 5].map((starValue) => (
                        <span
                          key={starValue}
                          className={`star ${
                            starValue <= review.rating ? "active" : ""
                          }`}
                          onClick={() => handleStarClick(starValue)}
                        >
                          â˜…
                        </span>
                      ))}
                    </div>

                    {/* <p style={{ marginBottom: "2px" }}>
                      Rating: {review.rating} stars
                    </p> */}
                    <p>{review.comment}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <div className="col-sm-4 col-sm-push-1">
            <h2>Leave a comment</h2>
            <form>
              <div className="form-group">
                <label>Review *</label>
                <input
                  type="text"
                  placeholder="New Review"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                />
              </div>
              <div className="form-group">
                <label>Rating *</label>
                <input
                  type="number"
                  placeholder="Rating"
                  value={newRating}
                  onChange={(e) => setNewRating(e.target.value)}
                />
              </div>

              <div className="form-group right-align">
                <button
                  className="btn btn-ghost"
                  variant="primary"
                  onClick={addItem}
                >
                  Add Comment
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>

      <div className="section small-padding border-top">
        <div className="container">
          <div className="row">
            <div className="col-sm-12 cta">
              <p>Need help? Contact our support team on</p>
              <p className="gradient-text">0330 123 4567</p>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Reviews;
